actionConfiguration.body:
declare @ResultCode nvarchar(10)
	,@ResultMessages NVARCHAR(MAX)
	,@INSERTED_PROFILE_ID NVARCHAR(MAX)

EXECUTE [dbo].[UPDATE_PMS_PROFILE_LM_V3]
   @pCOMPANY_ID = {{this.params.COMPANY_ID }}
  ,@pCOMPANY_PROFILE_AGREEMENT_NO = {{this.params.AGREEMENT_ID}}
  ,@pFORMULA_ID = {{this.params.FORMULA_ID}}
  ,@pINVENTORY_ID = {{this.params.INVENTORY_ID}}
  ,@pPROFILE_FLOOR_MODIFIER = {{this.params.PROFILE_FLOOR_MODIFIER}}
  ,@pPROFILE_PP_UNIT_MODIFIER = {{this.params.PROFILE_PP_UNIT_MODIFIER}}
	,@COMPANY_PROFILE_PERIOD_START = {{this.params.COMPANY_PROFILE_PERIOD_START}} 
	,@COMPANY_PROFILE_PERIOD_END = {{this.params.COMPANY_PROFILE_PERIOD_END}} 
	,@QUANTITY = {{this.params.QUANTITY||null}}
	,@FREQUENCY_TYPE = {{this.params.FREQUENCY_TYPE||null}}
	,@REPEAT_EVERY = {{this.params.REPEAT_EVERY||null}}
	,@FREQUENCY_DATE = {{this.params.FREQUENCY_DATE||null}}
	,@INVOICE_GROUP_ID = {{this.params.INVOICE_GROUP_ID||null}}
		,@PERIOD_END_FOR_CALCULATE = {{this.params.PERIOD_END_FOR_CALCULATE||null}}
		,@PERIOD_START_FOR_CALCULATE = {{this.params.PERIOD_START_FOR_CALCULATE||null}}
		,@PROFILE_PAYMENT_DUE_DATE = {{this.params.PROFILE_PAYMENT_DUE_DATE||null}}
	--,@RENEW_FROM_ID = 
	,@IS_INSERT = 1
  ,@USER_TOKEN = {{AppState.CurrentSession.SessionToken}}
	,@ResultCode =@ResultCode OUTPUT
	,@ResultMessages =@ResultMessages OUTPUT
	,@INSERTED_PROFILE_ID = @INSERTED_PROFILE_ID

SELECT @ResultCode AS RESULT_CODE,@INSERTED_PROFILE_ID as ID , @ResultMessages as RESULT_MESSAGES
