:root {
  font-family: "Nunito Sans";
}

.items-box-container {
  padding: 3px 3px;
  width: 100%;
  min-height: 68px;
  box-sizing: border-box;
}

.items-box-inner {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  align-items: flex-start;
}

.items-input {
  outline: none;
  border: 1px solid #e2e2e2;
  border-radius: 16px;
  min-width: 96px;
  margin-right: 8px;
  margin-left: 8px;
  box-sizing: border-box;
}
.items-input button {
  background: none;
  border: none;
  color: #c24447;
  cursor: pointer;
  margin-left: 4px;
  margin-right: 8px;
  font-size: 16px;
  padding: 0;
  line-height: 1;
}
.items-input input {
  border: 0px;
  border-top-left-radius: 16px;
  border-bottom-left-radius: 16px;
  padding: 7px 12px;
  font-size: 13px;
}

.item-chip {
  display: flex;
  align-items: center;
  background: #ffffff;
  color: var(--appsmith-theme-fontColor);
  border: 1px solid rgba(17, 17, 17, 0.0666666667);
  border-radius: 8px;
  padding: 6px 12px 6px 10px;
  font-size: calc(var(--appsmith-model-fontSize) * 1px);
  position: relative;
  margin: 0 4px 4px 0;
  white-space: nowrap;
}

.item-remove {
  background: none;
  border: none;
  color: #c24447;
  cursor: pointer;
  margin-left: 4px;
  font-size: 16px;
  padding: 0;
  line-height: 1;
}

.item-chip:last-child {
  margin-right: 0;
}

